    private void FCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FCActionPerformed
        // get the inputted integers        
        int a = Integer.parseInt(DegreesInFahrenheit.getText());
        //Complete the conversion formula
        double c = (a + 32) * 1.8;
        //output answer 
        DegreesInCelsius.setText(("") + c);
    }//GEN-LAST:event_FCActionPerformed

