    private void CFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CFActionPerformed
        // get the inputted integers        
        int b = Integer.parseInt(DegreesInCelsius.getText());
        //Complete the conversion formula
        double d = (b - 32) / 1.8;
        //output answer 
        DegreesInFahrenheit.setText(("") + d);
    }//GEN-LAST:event_CFActionPerformed

